// src/App.js
import React, { useState } from "react";
import { BrowserRouter as Router, Route, Routes, Navigate, Link } from "react-router-dom";
import Login from "./components/Login";
import Register from "./components/Register";
import AdminPage from "./components/AdminPage";
import NavBar from "./components/NavBar";
import "bootstrap/dist/css/bootstrap.min.css";
import "./App.css";  // Import the main CSS file

function App() {
  const [registering, setRegistering] = useState(false);
  const [loggedInUser, setLoggedInUser] = useState(null);
  const [isAdmin, setIsAdmin] = useState(false);

  return (
    <Router>
      <NavBar />
      <div className="container mt-5">
        <Routes>
          <Route path="/login" element={<Login setLoggedInUser={setLoggedInUser} setIsAdmin={setIsAdmin} />} />
          <Route path="/register" element={<Register setRegistering={setRegistering} />} />
          <Route path="/admin" element={isAdmin ? <AdminPage /> : <Navigate to="/login" />} />
          <Route path="/" element={
            loggedInUser ? (
              <div>
                <h1>Sesi√≥n Iniciada</h1>
                <p className="welcome-message">Bienvenido {loggedInUser}</p>
                {isAdmin && (
                  <Link to="/admin" className="btn btn-primary">Administrar Usuarios</Link>
                )}
              </div>
            ) : (
              <div>
                {registering ? (
                  <Register setRegistering={setRegistering} />
                ) : (
                  <Login setLoggedInUser={setLoggedInUser} setIsAdmin={setIsAdmin} isAdmin={isAdmin} />
                )}
                {!registering && (
                  <button className="btn btn-link" onClick={() => setRegistering(true)}>Crear Cuenta</button>
                )}
              </div>
            )
          } />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
