import React, { useState, useEffect } from "react";
import {
  BrowserRouter as Router,
  Route,
  Routes,
  Navigate,
  Link,
} from "react-router-dom";
import Login from "./components/Login";
import Register from "./components/Register";
import AdminPage from "./components/AdminPage";
import "bootstrap/dist/css/bootstrap.min.css";
import "./App.css";

function App() {
  const [registering, setRegistering] = useState(false);
  const [loggedInUser, setLoggedInUser] = useState(null);
  const [isAdmin, setIsAdmin] = useState(false);

  useEffect(() => {
    if (isAdmin) {
      window.location.href = "/admin";
    }
  }, [isAdmin]);

  return (
    <Router>
      <nav className="navbar navbar-expand-lg navbar-light bg-light">
        <div className="container-fluid">
          <Link className="navbar-brand" to="/">Electric Demeter Systems</Link>
          <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
            <span className="navbar-toggler-icon"></span>
          </button>
          <div className="collapse navbar-collapse" id="navbarNav">
            <ul className="navbar-nav ms-auto">
              <li className="nav-item">
                <Link className="nav-link" to="/login">Login</Link>
              </li>
              <li className="nav-item">
                <Link className="nav-link" to="/register">Register</Link>
              </li>
            </ul>
          </div>
        </div>
      </nav>
      <div className="container mt-5">
        <Routes>
          <Route
            path="/login"
            element={
              <Login
                setLoggedInUser={setLoggedInUser}
                setIsAdmin={setIsAdmin}
              />
            }
          />
          <Route
            path="/register"
            element={<Register setRegistering={setRegistering} />}
          />
          <Route
            path="/admin"
            element={isAdmin ? <AdminPage /> : <Navigate to="/login" />}
          />
          <Route
            path="/"
            element={
              loggedInUser ? (
                <div>
                  <h1>Sesión Iniciada</h1>
                  <p className="welcome-message">Bienvenido {loggedInUser}</p>
                </div>
              ) : (
                <div>
                  {registering ? (
                    <Register setRegistering={setRegistering} />
                  ) : (
                    <Login
                      setLoggedInUser={setLoggedInUser}
                      setIsAdmin={setIsAdmin}
                      isAdmin={isAdmin}
                    />
                  )}
                  {!registering && (
                    <button
                      className="btn btn-link"
                      onClick={() => setRegistering(true)}
                    >
                      Crear Cuenta
                    </button>
                  )}
                </div>
              )
            }
          />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
